mapper.select:
SELECT COUNT(A.SKU_ID) FROM (SELECT T.VENDER_ID, T.SKU_ID FROM T_TRADE T WHERE T.MARKET_DAY = #{marketDay}
AND VENDER_ID = #{venderId}
AND SUPPLIER_ID = #{supplierId}
GROUP BY T.VENDER_ID, T.SKU_ID) A

SELECT T.VENDER_ID, T.SKU_ID, SUM(T.NUM) NUM, T.MARKET_DAY FROM T_TRADE T WHERE T.MARKET_DAY = #{marketDay}
AND VENDER_ID = #{venderId}
AND SUPPLIER_ID = #{supplierId}
GROUP BY T.VENDER_ID, T.SKU_ID ORDER BY T.VENDER_ID DESC,NUM DESC LIMIT #{indexNum},#{pageSize}

Select count(T.SKU_ID)
FROM (
SELECT
DISTINCT SKU_ID
FROM
T_TRADE
WHERE
MARKET_DAY = #{marketDay}

    AND WARE_ID = #{wareId}


    AND SUPPLIER_ID = #{supplierId}

) T

SELECT D.NUM,D.SKU_ID,D.MARKET_DAY FROM(SELECT SUM(T.NUM) NUM, T.SKU_ID, T.MARKET_DAY FROM T_TRADE T WHERE T.MARKET_DAY = #{marketDay}
AND WARE_ID = #{wareId}
AND SUPPLIER_ID = #{supplierId}
GROUP BY T.SKU_ID ) D ORDER BY D.NUM DESC LIMIT #{indexNum},#{pageSize}

SELECT
COUNT(T.ID)
FROM
T_TRADE T
WHERE 1=1

    AND WARE_ID = #{wareId}


    AND VENDER_ID = #{venderId}


    AND MARKET_DAY = #{marketDay}


    AND SUPPLIER_ID = #{supplierId}

SELECT

FROM
T_TRADE T
WHERE 1=1

    AND WARE_ID = #{wareId}


    AND VENDER_ID = #{venderId}


    AND MARKET_DAY = #{marketDay}


    AND SUPPLIER_ID = #{supplierId}

ORDER BY
T.CREATED DESC
LIMIT #{indexNum},#{pageSize}
